function getCookie(c){const a=c+"=",d=decodeURIComponent(document.cookie),b=d.split(';');for(let d=0;d<b.length;d++){let c=b[d];while(c.charAt(0)===' ')c=c.substring(1);if(c.indexOf(a)===0)return c.substring(a.length,c.length)}return""}function setCookie(b,c,d){const a=new Date;a.setTime(a.getTime()+d*24*60*60*1e3);const e="expires="+a.toUTCString();document.cookie=b+"="+c+";"+e+";path=/"}const cookieBanner=document.querySelector('#cookie-banner'),hasCookieConsent=getCookie('cookies-consent');hasCookieConsent||cookieBanner.classList.remove('hidden');const consentCta=cookieBanner.querySelector('#consent-cookies');consentCta.addEventListener('click',()=>{cookieBanner.classList.add('hidden'),setCookie('cookies-consent',1,365)})